    // Example method to demonstrate accessing parsed data
    public static void printSportCount(SportCountRsp sportCountRsp) {
        List<SportCountRsp.CountItem> sportCount = sportCountRsp.getSportCount();
        for (SportCountRsp.CountItem item : sportCount) {
            System.out.println("Sport: " + item.sportName + ", Count: " + item.count);
            System.out.println("Event Group Types: " + item.eventGroupTypes.size());
            for (SportCountRsp.CountItem.EventGroupType eventGroup : item.eventGroupTypes) {
                System.out.println("  EventGroupTypeId: " + eventGroup.eventGroupTypeId + ", Count: " + eventGroup.count);
            }
            // Accessing programmeList as List<Map<String, Object>>
            System.out.println("Programme List: " + item.programmeList.size());
            for (Object programmeObj : item.programmeList) {
                if (programmeObj instanceof Map) {
                    Map<String, Object> programme = (Map<String, Object>) programmeObj;
                    System.out.println("  ProgrammeName: " + programme.get("ProgrammeName") + ", Count: " + programme.get("Count"));
                }
            }
        }
    }

